// نمط التحميل الموحد
export function UserProfile({ userId }: { userId: string }) {
  const { data: user, isLoading } = useUser(userId)

  if (isLoading) return <UserProfileSkeleton />
  if (!user) return <UserNotFound />

  return <UserProfileContent user={user} />
}

